function loadDataTable(){dataTable=$("#tblData").DataTable({ajax:{url:"/Admin/CoverType/GetAll"},columns:[{data:"name",width:"70%"},{data:"id",render:function(n){return`
                            <div class="d-flex justify-content-between">
                                <a href="/Admin/CoverType/Edit?id=${n}" asp-action="Edit" asp-route-id=@coverType.Id
                                    class="btn btn-outline-primary my-custom">
                                        <i class="fa-solid fa-pen-to-square"></i>
                                            Edit
                                </a>
                                <a onClick=Delete('/Admin/CoverType/Delete/${n}')
                                    class="btn btn-outline-danger my-custom" >
                                        <i class="fa-solid fa-trash"></i>
                                            Delete
                                </a>
                            </div>
                            `},width:"15%"},]})}function Delete(n){Swal.fire({title:"Are you sure?",text:"You won't be able to revert this!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(t=>{t.isConfirmed&&$.ajax({url:n,type:"DELETE",success:function(n){n.success?(dataTable.ajax.reload(),notyf.success(n.message)):notyf.error(n.message)}})})}var dataTable;$(document).ready(function(){loadDataTable()});