function loadDataTable(n){dataTable=$("#tblData").DataTable({ajax:{url:"/Admin/Order/GetAll?status="+n},columns:[{data:"id",width:"17%"},{data:"name",width:"15%"},{data:"phoneNumber",width:"15%"},{data:"appUser.email",width:"15%"},{data:"orderStatus",width:"10%"},{data:"orderTotal",width:"8%"},{data:"id",render:function(n){return`
                            <div class="d-flex justify-content-between">
                                <a href="/Admin/Order/Details?orderId=${n}" 
                                    class="btn btn-outline-primary w-100">
                                        <i class="fa-solid fa-circle-info"></i>
                                            Details
                                </a>
                            </div>
                            `},width:"10%"},]})}var dataTable;$(document).ready(function(){var n=window.location.search;n.includes("pending")&&loadDataTable("pending");n.includes("inprocess")&&loadDataTable("inprocess");n.includes("approved")&&loadDataTable("approved");n.includes("completed")&&loadDataTable("completed");n.includes("all")&&loadDataTable("all")});