function loadDataTable(){dataTable=$("#tblData").DataTable({ajax:{url:"/Admin/Product/GetAll"},columns:[{data:"title",width:"15%"},{data:"isbn",width:"15%"},{data:"price",width:"15%"},{data:"author",width:"15%"},{data:"category.name",width:"15%"},{data:"id",render:function(n){return`
                            <div class="d-flex justify-content-between">
                                <a href="/Admin/Product/Upsert?id=${n}" asp-action="Upsert" asp-route-id=@product.Id
                                    class="btn btn-outline-primary my-custom">
                                        <i class="fa-solid fa-pen-to-square"></i>
                                            Edit
                                </a>
                                <a onClick=Delete('/Admin/Product/Delete/${n}')
                                    class="btn btn-outline-danger my-custom" >
                                        <i class="fa-solid fa-trash"></i>
                                            Delete
                                </a>
                            </div>
                            `},width:"15%"},]})}function Delete(n){Swal.fire({title:"Are you sure?",text:"You won't be able to revert this!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(t=>{t.isConfirmed&&$.ajax({url:n,type:"DELETE",success:function(n){n.success?(dataTable.ajax.reload(),notyf.success(n.message)):notyf.error(n.message)}})})}var dataTable,notyf=new Notyf({position:{x:"right",y:"bottom"},duration:3e3,dismissible:!0,ripple:!0});$(document).ready(function(){loadDataTable()});